DROP SCHEMA IF EXISTS trabalho1_crud;
CREATE SCHEMA IF NOT EXISTS trabalho1_crud;

USE trabalho1_crud;

DROP TABLE IF EXISTS trabalho1_crud.products;
CREATE TABLE IF NOT EXISTS trabalho1_crud.products(
	id int auto_increment PRIMARY KEY,
    name_product VARCHAR(255) NOT NULL,
    description_product VARCHAR(255) NOT NULL,
    price float NOT NULL,
    stock int NOT NULL,
    available_online boolean NOT NULL
);


SELECT * FROM products WHERE id < 10;